import React from 'react';
import styled from 'styled-components';
import ThumbUpOutlinedIcon from '@mui/icons-material/ThumbUpOutlined';
import ThumbDownOffAltOutlinedIcon from '@mui/icons-material/ThumbDownOffAltOutlined';

const Container = styled.div`
  display: flex;
  gap: 12px;
`;

const Content = styled.div`
   flex: 5;
`;

const VideoWrapper = styled.div`

`;

const Title = styled.h1`
   font-size: 16px;
   font-weight: 500;
   margin-top: 10px;
   margin-bottom: 5px;
`;

const Details = styled.div`
   display: flex;
   align-items: center;
   justify-content: space-between;
   font-size: 14px;
`;
const Info = styled.span``;
const Buttons = styled.div``;
const Button = styled.div``;

const Recommendation = styled.div`
   flex: 2;
`;

const Video = () => {
  return (
    <Container>
        <Content>
           <VideoWrapper>
           <iframe width="100%" height="400" src="https://www.youtube.com/embed/yIaXoop8gl4" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
           </VideoWrapper>
           <Title>Test video for the day</Title>
           <Details>
              <Info>23,56,67 views Jun 22, 2022</Info>
              <Buttons>
                  <Button><ThumbUpOutlinedIcon/> 34,24</Button>
                  <Button><ThumbDownOffAltOutlinedIcon/> 34,24</Button>
              </Buttons>
           </Details>
        </Content>
        <Recommendation>
         Recommendation
        </Recommendation>
    </Container>
  )
}

export default Video
