import React from 'react';
import styled from 'styled-components';
import ThumbUpOutlinedIcon from '@mui/icons-material/ThumbUpOutlined';
import ThumbDownOffAltOutlinedIcon from '@mui/icons-material/ThumbDownOffAltOutlined';
import LibraryAddOutlinedIcon from '@mui/icons-material/LibraryAddOutlined';
import ReplyOutlinedIcon from '@mui/icons-material/ReplyOutlined';

const Container = styled.div`
  display: flex;
  gap: 12px;
`;

const Content = styled.div`
   flex: 5;
`;

const VideoWrapper = styled.div`

`;

const Title = styled.h1`
   font-size: 16px;
   font-weight: 500;
   margin-top: 10px;
   margin-bottom: 5px;
   color: #fff;
`;

const Details = styled.div`
   display: flex;
   align-items: center;
   justify-content: space-between;
   font-size: 14px;
`;
const Info = styled.span`
   color: #757575;
`;
const Buttons = styled.div`
   display: flex;
   align-items: center;
   justify-content: center;
   gap: 25px;
   color: #757575;
`;
const Button = styled.div`
   display: flex;
   align-items: center;
   justify-content: center;
   gap: 5px;
   cursor: pointer;
   font-size: 14px;
`;

const Recommendation = styled.div`
   flex: 2;
`;

const Hr = styled.span`
   display: block;
   margin: 15px 0px;
   border-bottom: 1px solid #757575;
`;

const Channel = styled.div`
   display: flex;
   align-items: center;
   justify-content: space-between;
`;

const Subscribe = styled.div`
   
`;

const ChanelInfo = styled.div`
   
`;

const Video = () => {
  return (
    <Container>
        <Content>
           <VideoWrapper>
           <iframe width="100%" height="400" src="https://www.youtube.com/embed/yIaXoop8gl4" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
           </VideoWrapper>
           <Title>Test video for the day</Title>
           <Details>
              <Info>23,56,67 views Jun 22, 2022</Info>
              <Buttons>
                  <Button><ThumbUpOutlinedIcon style={{fontSize: '18px'}}/> 34,24</Button>
                  <Button><ThumbDownOffAltOutlinedIcon style={{fontSize: '18px'}}/> Dislike</Button>
                  <Button><ReplyOutlinedIcon style={{fontSize: '18px'}}/> Share</Button>
                  <Button><LibraryAddOutlinedIcon style={{fontSize: '18px'}}/> Save</Button>
              </Buttons>
           </Details>
           <Hr/>
           <Channel>
              <ChanelInfo></ChanelInfo>
           </Channel>
        </Content>
        <Recommendation>
         Recommendation
        </Recommendation>
    </Container>
  )
}

export default Video
